operations.schema:
input EditPhaseInput {
    phaseIndex: Int!
	actuals: GranteeActuals
	planned: GranteePlanned
	stats: GranteeStats
	status: Status
}

# add new types here
enum Status {
	Uninitialized
	NotStarted
	InProgress
	Finalized
}

type GranteeActuals {
	arbReceived: Float
	arbUtilized: Float
	arbRemaining: Float
	contractsIncentivized: [Contract]
	summary: String
	disclosures: String
}

type GranteePlanned {
	arbToBeDistributed: Float
	contractsIncentivized: [Contract]
	distributionMechanism: [DistributionMechanism]
	summary: String
	summaryOfChanges: String
}

type GranteeStats {
	avgDailyTVL: Float
	avgDailyTXNS: Float
	avgDailyVolume: Float
	uniqueAddressesCount: Float
	transactionFees: Float
}

type Contract {
	contractId: ID!
	contractLabel: String
	contractAddress: String
}

enum DistributionMechanism {
	LPIncentives
	Airdrop
}

input AddEditorInput {
    editorAddress: String!
}

# add new types here

input RemoveEditorInput {
    editorAddress: String!
}

