operations.schema:
input EditPhaseInput {
    phaseIndex: Int!
	actuals: GranteeActuals
	planned: GranteePlanned
	stats: GranteeStats
	status: Status
}

# add new types here
enum Status {
	Uninitialized
	NotStarted
	InProgress
	Finalized
}

type GranteeActuals {
	arbReceived: Float
	arbUtilized: Float
	arbRemaining: Float
	contractsIncentivized: [Contract]
	incentives: String
	summary: String
	disclosures: String
}

type GranteePlanned {
	arbToBeDistributed: Float
	contractsIncentivized: [Contract]
	distributionMechanism: [DistributionMechanism]
	changes: String
  	expectations: String
}

type GranteeStats {
	avgDailyTVL: Float
  avgDailyTXNS: Float
  avgDailyUniqueUsers: Float
  kpis: [NamedKpi]
  changes: String
  lessons: String
}

type NamedKpi {
  name: String
  value: Float
}

type Contract {
  contractId: ID!
  contractLabel: String
  contractAddress: String
}

enum DistributionMechanism {
	LPIncentives
	Airdrop
}

input AddEditorInput {
    editorAddress: String!
}

